    }

    function index(){
        $param = I('param.');
        $tenant_id = isset($param['tenant_id']) ? $param['tenant_id'] : getTenantIds();
        $redis = connectionRedis();

        if(IS_POST){
            if(!$param['zone']){
                $this->error('请选择国家');
            }
            $arr = explode('-',$param['zone']);
            $param['zone'] = $arr[0];
            $limit_num = $arr[1];
            if($param['num'] < 1 || $param['num'] > $limit_num){
                $this->error('请输入数量范围：1 - '.$limit_num);
            }
            if($param['level_start'] > $param['level_end']){
                $this->error('等级区间错误');
            }
            if($param['level_start'] < 1 || $param['level_start'] > 9999){
                $this->error('等级区间错误：1 - 9999');
            }
            if($param['level_end'] < 1 || $param['level_end'] > 9999){
                $this->error('等级区间错误：1 - 9999');
            }
            $level_start_arr = getLevelList($tenant_id,$param['level_start']);
            $level_end_arr = getLevelList($tenant_id,$param['level_end']);
           /* if(!isset($level_start_arr['experience'])){
                $this->error('没有该等级：'.$param['level_start']);
            }
            if(!isset($level_end_arr['experience'])){
                $this->error('没有该等级：'.$param['level_end'].' | '.json_encode($level_end_arr));
            }*/
            $experience_start = intval($level_start_arr['experience']);
            $experience_end = intval($level_end_arr['experience']);

            try{
                $virtualname_list = M('virtualname')->where(['zone'=>$param['zone']])->order('rand()')->limit(10000)->select();
                if(count($virtualname_list) <= 0){
                    $this->error('没有昵称库');
                }

                $tenant_info = getTenantInfo($tenant_id);

                // 清除掉虚拟会员
                M("users")->where(['user_type'=>3,'tenant_id'=>$tenant_id])->delete();
                // 清除缓存
                $redis->del('virtual_user_'.$tenant_id);

                for($i=0;$i<intval($param['num']);$i++){
                    $timeinfo = date('mdHis',time());
                    $mt_nickname = rand(0,count($virtualname_list)-1);
                    $mt_head = rand(1,200);
                    if($mt_head>100){
                        $avatar = $tenant_info['site'].'/public/images/head_'.$mt_head.'.png';
                    }else{
                        $avatar = $tenant_info['site'].'/public/images/head_'.$mt_head.'.jpg';
                    }

                    $name = isset($virtualname_list[$i]) ? $virtualname_list[$i]['name'] : $virtualname_list[$mt_nickname]['name'];

                    $data['user_login'] = $timeinfo.uniqid();
                    $data['mobile']= $timeinfo.$i;
                    $data['user_nicename'] = $name;
                    $data['user_pass'] = setPass('abc123456');
                    $data['signature'] = $name;
                    $data['avatar'] = $avatar;
                    $data['avatar_thumb'] = $avatar;
                    $data['last_login_ip'] = $_SERVER['REMOTE_ADDR'];
                    $data['create_time'] = date("Y-m-d H:i:s");
                    $data['ctime'] = time();
                    $data['user_status'] = 1;
                    $data['user_type']=3;   // 第三类型，标识 虚拟会员
                    $data['source'] ='pc';
                    $data["coin"]=10000;
                    $data["consumption"]=rand($experience_start,$experience_end);
                    $data["tenant_id"]=1;
                    $data["follows"]= rand(1, 100);
                    $data["fans"]= rand(1, 100);
                    $data["votestotal"]= rand(10, 1000);
                    $data['game_tenant_id'] = $tenant_info['game_tenant_id'];
                    $data['zone']=$param['zone'];
                    $data['tenant_id'] = $tenant_id;

                    $res = M("users")->add($data);
                    if($res){
                        $virtual_user = $data;
                        $virtual_user['id'] = M("users")->getLastInsID();
                        $virtual_user['level'] = rand($param['level_start'],$param['level_end']);
                        $redis->LPush('virtual_user_'.$tenant_id,json_encode($virtual_user));
                    }
                }
            }catch (\Exception $e){
                $this->error('操作失败：'.$e->getMessage());
            }
            UsersCache::getInstance()->DelVirtualUserListCche($tenant_id); // 虚拟会员清除缓存golang
            $this->success('操作成功',U('index',array('menuid'=>$param['menuid'],'tenant_id'=>$tenant_id)));
        }

        $config=getConfigPri();

        $country_list = M('virtualname')->field('zone,count(id) as num')->group('zone')->select();
        $country_list = array_column($country_list,null,'zone');

        foreach ($country_list as $key=>$val){
            if($val['num'] > 3000){
                $country_list[$key]['num'] = 3000;
            }
            $country_list[$key]['name'] = country($val['zone'])['sc'];
        }

		$this->assign('config', $config);
        $this->assign('country_list', $country_list);
        $this->assign('tenant_list',getTenantList());
        $this->assign('role_id',getRoleId());
        $this->assign('tenant_id',$tenant_id);
        $this->assign('param',$param);
    	$this->display("edit");
    }

